function drw_gear(t,e,a,n,i,r){t.beginPath(),t.arc(e,a,n,0,2*Math.PI,!1),t.fillStyle="#999",t.fill();for(var o=0;r>o;){var h,d,l=2*o*Math.PI/r;h=e+i*Math.cos(l),d=a+i*Math.sin(l),drw_spoke(t,e,a,h,d),o++}t.beginPath(),t.arc(e,a,n/2,0,2*Math.PI,!1),t.fillStyle="#888",t.fill()}function drw_spoke(t,e,a,n,i){t.beginPath(),t.moveTo(e,a),t.lineTo(n,i),t.lineWidth=5,t.lineCap="round",t.strokeStyle="#999",t.stroke()}$(document).ready(function(){var t=document.getElementById("bgfill");if(t.getContext){var e=t.getContext("2d");e.canvas.width=window.innerWidth,e.canvas.height=window.innerHeight;for(var a=0,n=0,i=30,r=35,o=19,h=90;a-i<e.canvas.width;){for(;n-i<e.canvas.height;)drw_gear(e,a,n,i,r,o),n+=h;for(a+=h,n=h/2;n-i<e.canvas.height;)drw_gear(e,a,n,i,r,o),n+=h;a+=h,n=0}}});